SELECT CASE WHEN q . type = '1' THEN '单选题' WHEN q . type = '2' THEN '多选题' WHEN q . type = '3' THEN '判断题' ELSE '不祥' END AS "题型" , q . title AS "问题" , CASE WHEN q . rightflag = '1' AND q . type = '3' AND q . useranswer = '0' THEN 'B. 错误' WHEN q . rightflag = '1' AND q . type = '3' AND q . useranswer = '1' THEN 'A. 正确' WHEN q . rightflag = '1' AND q . type = '1' THEN ( SELECT GROUP_CONCAT ( char ( 65 + a . sort ) || '. ' || a . content , '    ' ) FROM answer_option_list a WHERE a . qid = q . id ) WHEN q . rightflag = '1' AND q . type = '2' THEN ( SELECT GROUP_CONCAT ( char ( 65 + a . sort ) || '. ' || a . content , '    ' ) FROM answer_option_list a WHERE a . qid = q . id ) ELSE '赞无答案' END AS "正确答案" , CASE WHEN q . type = '3' THEN 'A. 正确  B. 错误' ELSE GROUP_CONCAT ( char ( 65 + a . sort ) || '. ' || a . content , '    ' ) END AS "选项" FROM question_list q LEFT JOIN question_option_list a ON q . id = a . qid GROUP BY q . title